CREATE TABLE IF NOT EXISTS status(
	pStatus INTEGER AUTO_INCREMENT,
	cStatus varchar(20) NOT NULL,
	PRIMARY KEY (pStatus)
);

CREATE TABLE IF NOT EXISTS student (
	pStudent INTEGER AUTO_INCREMENT,
  	cName varchar(50) NOT NULL,
  	cPassword varchar(50) NOT NULL,
  	cEmail varchar(100) NOT NULL,
  	cAddress varchar(255),
  	pStatus INTEGER,
  	tDesc text,
  	cLogo mediumtext DEFAULT ''),
  	cThumbnail text DEFAULT ''),
  	dLastLogin TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	dCreated TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	PRIMARY KEY (pStudent),
  	FOREIGN KEY (pStatus) REFERENCES status(pStatus),
  	CONSTRAINT uc_cemail UNIQUE (cEmail)
);

create index idx_student_pstudent on student(pStudent);

CREATE TABLE IF NOT EXISTS teacher (
	pTeacher INTEGER AUTO_INCREMENT,
  	cName varchar(50) NOT NULL,
  	cPassword varchar(50) NOT NULL,
  	cEmail varchar(100) NOT NULL,
  	cAddresse varchar(255),
  	pStatus INTEGER,
  	tDesc text,
  	cLogo mediumtext,
  	cThumbnail text,
  	dLastLogin TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	dCreated TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	PRIMARY KEY (pTeacher),
  	FOREIGN KEY (pStatus) REFERENCES status(pStatus),
  	CONSTRAINT uc_cemail UNIQUE (cEmail)
);

create index idx_teacher_pteacher on teacher(pTeacher);

CREATE TABLE IF NOT EXISTS article (
	pArticle INTEGER AUTO_INCREMENT,
	cTitle varchar(255) NOT NULL,
	cArticle mediumtext NOT NULL,
	pOwner INTEGER,
  	dModified TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	dCreated TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	PRIMARY KEY (pArticle),
  	FOREIGN KEY (pOwner) REFERENCES teacher(pTeacher),
);

create index idx_article_particle on article(pArticle);

CREATE TABLE IF NOT EXISTS course (
	pCourse INTEGER AUTO_INCREMENT,
	cSigle varchar(10) NOT NULL,
	cTitle varchar(100) NOT NULL,
	tDesc text NOT NULL,
  	PRIMARY KEY (pTeacher),
  	CONSTRAINT uc_csigle UNIQUE (cSigle)
);

CREATE TABLE IF NOT EXISTS course_teacher (
	pCourse INTEGER,
	pTeacher INTEGER,
	FOREIGN KEY (pCourse) REFERENCES course(pCourse),
	FOREIGN KEY (pTeacher) REFERENCES teacher(pTeacher),
);

CREATE TABLE IF NOT EXISTS comment (
	pComment INTEGER AUTO_INCREMENT,
	cComment text NOT NULL,
	pTeacher INTEGER,
	pStudent INTEGER,
  	pCourse INTEGER DEFAULT '0',
  	dCreated TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	PRIMARY KEY (pArticle),
  	FOREIGN KEY (pTeacher) REFERENCES teacher(pTeacher),
  	FOREIGN KEY (pStudent) REFERENCES student(pStudent),
);

create index idx_comment_pcomment on comment(pComment);

CREATE TABLE IF NOT EXISTS employer (
	pEmployer INTEGER AUTO_INCREMENT,
  	cName varchar(50) NOT NULL,
  	cPassword varchar(50) NOT NULL,
  	cEmail varchar(100) NOT NULL,
  	cAddresse varchar(255),
  	tDesc text,
  	cLogo mediumtext,
  	cThumbnail text,
  	dLastLogin TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	dCreated TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	PRIMARY KEY (pEmployer),
  	CONSTRAINT uc_cemail UNIQUE (cEmail)
);

create index idx_employer_pemployer on employer(pEmployer);

CREATE TABLE IF NOT EXISTS job (
	pJob INTEGER AUTO_INCREMENT,
	cTitle varchar(255) NOT NULL,
	cDesc text NOT NULL,
	pOwner INTEGER,
  	dModified TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	dCreated TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	PRIMARY KEY (pJob),
  	FOREIGN KEY (pOwner) REFERENCES employer(pEmployer),
);

create index idx_job_pjob on job(pJob);

CREATE TABLE IF NOT EXISTS department (
	pDepartment INTEGER AUTO_INCREMENT,
  	cName varchar(50) NOT NULL,
  	cPassword varchar(50) NOT NULL,
  	cEmail varchar(100) NOT NULL,
  	cAddresse varchar(255),
  	tDesc text,
  	cLogo mediumtext,
  	cThumbnail text,
  	dLastLogin TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	dCreated TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  	PRIMARY KEY (pDepartment),
  	CONSTRAINT uc_cemail UNIQUE (cEmail)
);

create index idx_department_pdepartment on department(pDepartment);

CREATE TABLE IF NOT EXISTS course_department (
	pCourse INTEGER,
	pDepartment INTEGER,
	FOREIGN KEY (pCourse) REFERENCES course(pCourse),
	FOREIGN KEY (pDepartment) REFERENCES department(pDepartment),
);

CREATE TABLE IF NOT EXISTS teacher_department (
	pTeacher INTEGER,
	pDepartment INTEGER,
	FOREIGN KEY (pTeacher) REFERENCES teacher(pTeacher),
	FOREIGN KEY (pDepartment) REFERENCES department(pDepartment),
);